$(document).ready(function(){$(window).scroll(function(){getPageLength();});function getPageLength(){var completeHeight=$(document).height();var viewportHeight=$(window).height();var scrollTop=$(this).scrollTop();var diff=completeHeight-scrollTop;if(diff<=viewportHeight){getNextPage();}}
function getNextPage(){var nextLink=$(".next").attr("href");var nextLinkClass=".next";var thisList=$("#productList");if(nextLink!="#"){thisList.append("<div class=\"loading\" style=\"height:400px;\">Loading...</div>");$.ajax({url:nextLink,context:document.body,success:function(result){thisList.append($(result).find("#productList").html());if($(result).find(nextLinkClass).attr("href")==undefined)
$(nextLinkClass).attr("href","#");else
$(nextLinkClass).attr("href",$(result).find(nextLinkClass).attr("href"));},complete:function(){$(".loading").remove();}});}}});